  const showProductPage = () => {
    setClickProduct(clickProduct)
    console.log(clickProduct)
  }






    return (
        <React.Fragment>
        <div className='store-page'>
            <h1 className='page-title'>SHOP</h1>
            <div className={`product-list ${clickProduct && 'hide-product-list'}`}>
                <div className='product-container'>
                    <img id="product-img" src={Hero} alt="grateful dead logo" img/>
                    <h2 id='product-title'>8/26/23 Tickets</h2>
                    <button onClick={showProductPage} id='add-to-cart-btn'>Add To Cart</button>
                </div>
            </div>
        </div>
        </React.Fragment>
      )
}


