import { Link } from "react-router-dom"
import { FaBars, FaTimes } from "react-icons/fa";
import { useState } from "react";

export const Navbar = () => {
  // State to control visibility of the navigation links
  const [showLink, setShowLink] = useState(false)
  // State to control visibility of the navigation bar
  const [showNavBar, setShowNavBar] = useState(false)
  // State to control style of selected link
  const [selectedLink, setSelectedLink] = useState(null)

  // Function to toggle the visibility of the navigation bar and links
	const showNavbar = () => {
    setShowNavBar(!showNavBar)
    setShowLink(!showLink)
	};

  // Function to close the navigation bar and bring user to selected link
  const closeNavBar = () => {
    setShowNavBar(false)
    setShowLink(false)
  }

  const handleLinkClick = (event) => {
    setSelectedLink(event.target)
    closeNavBar()
  }

	return (
    <header>
      <Link to="/" className="site-title">Dead To The Core</Link>
      <nav className={`nav ${showNavBar && "responsive_nav"}`}>
        {/* add the "selected" class to the selected link */}
        {showLink && (
          <>
            <Link to="/" onClick={handleLinkClick} className={selectedLink === "Home" && "selected"}>Home</Link>
            <Link to="/band" onClick={handleLinkClick} className={selectedLink === "Band" && "selected"}>Band</Link>
            <Link to="/tour" onClick={handleLinkClick} className={selectedLink === "Tour" && "selected"}>Tour</Link>
            <Link to="/watch" onClick={handleLinkClick} className={selectedLink === "Watch" && "selected"}>Watch</Link>
            <Link to="/contact" onClick={handleLinkClick} className={selectedLink === "Contact" && "selected"}>Contact</Link>
          </>
        )}
        <button className="nav-btn nav-close-btn" onClick={showNavbar}>
          <FaTimes />
        </button>
      </nav>
      <button className="nav-btn" onClick={showNavbar}>
        <FaBars />
      </button>
    </header>
  );
}
